import { useState } from "react";
import Head from "next/head";
import dynamic from "next/dynamic";
import styles from "./Home.module.css";
import breakingdoor from "./breakingdoor.gif";
const WithSSR = dynamic(() => import("../components/WithSSR"));
const WithoutSSR = dynamic(() => import("../components/WithoutSSR"), {
  ssr: false,
});
const HereIsJonny = dynamic(() => import("../components/HereIsJonny"));

export default function Home() {
  const [isBroked, setIsBroked] = useState(false);

  return (
    <div>
      <Head>
        <title>Create Next App</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>

      <>
        <main className={styles.main}>
          <h1 className={styles.title}>
            This is a danymic import Nextjs sample
          </h1>

          <div className={styles.grid}>
            <div>
              <div className={styles.flex}>
                <h3>
                  Let's start with a simple lazy require example of a react
                  componente
                </h3>
                <p>
                  It suppose to load the bundle of this component only after you
                  click the button. You can follow the loading on the network
                  tab on dev tools
                </p>
                <button
                  className={styles.button}
                  onClick={() => setIsBroked(true)}
                >
                  Break the door
                </button>
                <div>
                  {!isBroked ? (
                    <img height="200" width="300" src={breakingdoor} />
                  ) : (
                    <HereIsJonny />
                  )}
                </div>
              </div>
            </div>
            <hr className={styles.divider} />
            <div>
              <div>
                <WithSSR />
              </div>
            </div>
            <hr className={styles.divider} />
            <div>
              <WithoutSSR />
            </div>
          </div>
        </main>
      </>
    </div>
  );
}
